// src/Components/Trainer/Trainer.js
import React, { useState, useEffect } from "react";
import { createTrainer, getTrainers } from "./TrainerService";
import TrainerList from "./MainList";
import "./Trainer.css"; // Import the CSS file for styling
import { getPokemons } from "../Pokémon/PokémonService";

const Trainer = () => {
  const [trainers, setTrainers] = useState([]);
  const [newTrainerName, setNewTrainerName] = useState("");
  const [pokemons, setPokemons] = useState([]);
  const [selectedPokemon, setSelectedPokemon] = useState("");

  useEffect(() => {
    getTrainers().then((fetchedTrainers) => {
      setTrainers(fetchedTrainers);
    });
    getPokemons().then((fetchedPokemons) => {
      setPokemons(fetchedPokemons);
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newTrainerName.trim()) {
      createTrainer(newTrainerName, selectedPokemon).then(() => {
        getTrainers().then((updatedTrainers) => {
          setTrainers(updatedTrainers);
        });
        setNewTrainerName("");
        setSelectedPokemon("");
      });
    } else {
      alert("Please fill out the Trainer's name.");
    }
  };

  return (
    <div className="container">
      <div className="left">
        <h3>Submit New Trainer</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={newTrainerName}
            onChange={(e) => setNewTrainerName(e.target.value)}
            placeholder="Enter Trainer Name"
          />
          <select
            value={selectedPokemon}
            onChange={(e) => setSelectedPokemon(e.target.value)}
          >
            <option value="">Select Pokémon</option>
            {pokemons.map((pokemon) => (
              <option key={pokemon.id} value={pokemon.name}>
                {pokemon.name}
              </option>
            ))}
          </select>
          <button type="submit">Add Trainer</button>
        </form>
      </div>
      <div className="divider"></div>
      <div className="right">
        <h3>Trainer List</h3>
        <TrainerList trainers={trainers} />
      </div>
    </div>
  );
};

export default Trainer;
