import React, { useState, useEffect } from "react";
import { getPokemon, createPokemon, deletePokemon } from "./PokemonService";
import MainList from "./MainList";
import "./Pokemon.css"; // Ensure this CSS file exists in the same folder

const Pokemon = () => {
  const [pokemons, setPokemons] = useState([]);
  const [newPokemonName, setNewPokemonName] = useState("");
  const [newPokemonType, setNewPokemonType] = useState("");
  const [newPokemonLocation, setNewPokemonLocation] = useState("");

  useEffect(() => {
    getPokemon().then((fetchedPokemons) => {
      setPokemons(fetchedPokemons);
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newPokemonName && newPokemonType && newPokemonLocation) {
      createPokemon(newPokemonName, newPokemonType, newPokemonLocation).then(() => {
        getPokemon().then((updatedPokemons) => {
          setPokemons(updatedPokemons);
        });
        setNewPokemonName("");
        setNewPokemonType("");
        setNewPokemonLocation("");
      });
    } else {
      alert("Please fill out all fields.");
    }
  };

  return (
    <div className="container">
      <div className="left">
        <h3>Submit New Pokémon</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={newPokemonName}
            onChange={(e) => setNewPokemonName(e.target.value)}
            placeholder="Enter Pokémon Name"
          />
          <input
            type="text"
            value={newPokemonType}
            onChange={(e) => setNewPokemonType(e.target.value)}
            placeholder="Enter Pokémon Type"
          />
          <input
            type="text"
            value={newPokemonLocation}
            onChange={(e) => setNewPokemonLocation(e.target.value)}
            placeholder="Enter Location Found"
          />
          <button type="submit">Add Pokémon</button>
        </form>
      </div>
      <div className="divider"></div>
      <div className="right">
        <h3>Pokémon List</h3>
        <MainList pokemons={pokemons} setPokemons={setPokemons} />
      </div>
    </div>
  );
};

export default Pokemon;
