// src/Components/LearnService.js

import Parse from "parse";

// Function to fetch all Pokémon
export const getPokemon = () => {
  const Pokemon = Parse.Object.extend("Pokemon");
  const query = new Parse.Query(Pokemon);
  return query
    .find()
    .then((results) => {
      return results.map((item) => ({
        id: item.id,
        name: item.get("Name"),
        type: item.get("Type"),
        location: item.get("Location"),
      }));
    })
    .catch((error) => {
      console.log("Error fetching Pokémon: ", error);
    });
};

// Function to create a new Pokémon
export const createPokemon = (name, type, location) => {
  const Pokemon = Parse.Object.extend("Pokemon");
  const pokemon = new Pokemon();
  pokemon.set("Name", name);
  pokemon.set("Type", type);
  pokemon.set("Location", location);
  return pokemon.save();
};

// Function to delete a Pokémon by ID
export const deletePokemon = (id) => {
  const Pokemon = Parse.Object.extend("Pokemon");
  const query = new Parse.Query(Pokemon);
  return query.get(id).then((pokemon) => {
    return pokemon.destroy();
  });
};
