import React, { useState, useEffect } from "react";
import { getAllNames, createName } from "./LearnService"; // Import createName
import MainList from "./MainList";

const Main = () => {
  const [names, setNames] = useState([]);
  const [newName, setNewName] = useState(""); // State for new name input

  // UseEffect to fetch data when the component loads
  useEffect(() => {
    getAllNames().then((fetchedNames) => {
      setNames(fetchedNames);
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent default form submission
    if (newName.trim()) { // Check if the input is not empty
      createName(newName).then((result) => {
        setNames((prevNames) => [...prevNames, result]); // Update state with the new name
        setNewName(""); // Clear input field
      });
    } else {
      alert("Please enter a name."); // Alert user if the name is empty
    }
  };

  return (
    <div>
      <h1>This is the main stateful parent component.</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={newName}
          onChange={(e) => setNewName(e.target.value)} // Update state on input change
          placeholder="Enter a name"
          required
        />
        <button type="submit">Submit</button>
      </form>
      <MainList names={names} />
    </div>
  );
};

export default Main;
