import React, { useState, useEffect } from "react";
import { getPokemon, createPokemon, deletePokemon } from "./PokemonService"; // Ensure you're importing from the correct service
import MainList from "./MainList";
import "./Main.css"; // Import the CSS file for styling

const Pokemon = () => {
  const [pokemons, setPokemons] = useState([]);
  const [newName, setNewName] = useState("");
  const [newType, setNewType] = useState("");
  const [newLocation, setNewLocation] = useState("");

  useEffect(() => {
    loadPokemon();
  }, []);

  const loadPokemon = () => {
    getPokemon().then((fetchedPokemon) => {
      setPokemons(fetchedPokemon);
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newName.trim() && newType.trim() && newLocation.trim()) {
      createPokemon(newName, newType, newLocation).then(() => {
        loadPokemon();
        setNewName("");
        setNewType("");
        setNewLocation("");
      });
    } else {
      alert("Please fill out all fields.");
    }
  };

  const handleDelete = (id) => {
    deletePokemon(id).then(() => {
      loadPokemon(); // Refresh the list after deletion
    });
  };

  const handleDuplicate = (pokemon) => {
    setNewName(pokemon.name);
    setNewType(pokemon.type);
    setNewLocation(pokemon.location);
  };

  return (
    <div className="container">
      <div className="form-container">
        <h3>Submit New Pokémon</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
            placeholder="Enter Pokémon Name"
          />
          <input
            type="text"
            value={newType}
            onChange={(e) => setNewType(e.target.value)}
            placeholder="Enter Pokémon Type"
          />
          <input
            type="text"
            value={newLocation}
            onChange={(e) => setNewLocation(e.target.value)}
            placeholder="Enter Location Found"
          />
          <button type="submit">Add Pokémon</button>
        </form>
      </div>

      <div className="divider"></div> {/* Optional for layout separation */}

      <div className="list-container">
        <h3>Pokémon List</h3>
        <MainList pokemons={pokemons} onDelete={handleDelete} onDuplicate={handleDuplicate} />
      </div>

      <nav className="bottom-nav">
        <a href="/trainer">Trainer Page</a>
        <a href="/pokemon">Pokémon Page</a>
      </nav>
    </div>
  );
};

export default Pokemon;
