// src/Components/Pokemon.js

import React, { useState, useEffect } from "react";
import { getPokemon, createPokemon } from "./LearnService";
import MainList from "./MainList";
import "./Main.css"; // Import the CSS file for styling

const Pokemon = () => {
  const [pokemons, setPokemons] = useState([]);
  const [newName, setNewName] = useState("");
  const [newType, setNewType] = useState("");
  const [newLocation, setNewLocation] = useState("");

  useEffect(() => {
    getPokemon().then((fetchedPokemon) => {
      setPokemons(fetchedPokemon);
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newName.trim() && newType.trim() && newLocation.trim()) {
      createPokemon(newName, newType, newLocation).then((result) => {
        getPokemon().then((updatedPokemon) => {
          setPokemons(updatedPokemon);
        });
        setNewName("");
        setNewType("");
        setNewLocation("");
      });
    } else {
      alert("Please fill out all fields.");
    }
  };

  return (
    <div className="container">
      <div className="left">
        <h3>Submit New Pokémon</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
            placeholder="Enter Pokémon Name"
          />
          <input
            type="text"
            value={newType}
            onChange={(e) => setNewType(e.target.value)}
            placeholder="Enter Pokémon Type"
          />
          <input
            type="text"
            value={newLocation}
            onChange={(e) => setNewLocation(e.target.value)}
            placeholder="Enter Location Found"
          />
          <button type="submit">Add Pokémon</button>
        </form>
      </div>
      <div className="divider"></div>
      <div className="right">
        <h3>Pokémon List</h3>
        <MainList pokemons={pokemons} />
      </div>
    </div>
  );
};

export default Pokemon;
