// src/Components/LearnService.js

import Parse from "parse";

// Function to get all Pokémon from Parse
export const getPokemon = () => {
  const Pokemon = Parse.Object.extend("Pokemon");
  const query = new Parse.Query(Pokemon);
  return query
    .find()
    .then((results) => {
      return results.map((item) => ({
        id: item.id,
        name: item.get("Name"),
        type: item.get("Type"),
        location: item.get("Location")
      }));
    })
    .catch((error) => {
      console.log("error: ", error);
    });
};

// Function to create a new Pokémon entry
export const createPokemon = (Name, Type, Location) => {
  console.log("Creating: ", { Name, Type, Location });
  const Pokemon = Parse.Object.extend("Pokemon");
  const query = new Pokemon();
  query.set("Name", Name);
  query.set("Type", Type);
  query.set("Location", Location);
  return query.save().then((result) => {
    return result;
  });
};
