import Parse from "parse";

//separate parse model for each class
// Function to get all names from Parse

export const getPeople = () => {
  const Feature4 = Parse.Object.extend("People");
  const query = new Parse.Query(Feature4);
  return query
    .find()
    .then((results) => {
      return results.map((item) => ({
        id: item.id,
        name: item.get("Name"), // Assuming "name" is a field in your class
      }));
    })
    .catch((error) => {
      console.log("error: ", error);
    });
};

export const createPerson = (Name) => {
  console.log("Creating: ", Name);
  const People = Parse.Object.extend("People");
  const query = new People();
  // using setter to UPDATE the object
  query.set("Name", Name);
  return query.save().then((result) => {
    // returns new Lesson object
    return result;
  });
};
