import React, { useState, useEffect } from "react";
import { createName, getAllNames } from "./LearnService";
import MainList from "./MainList";

const Main = () => {
  const [lessons, setLessons] = useState([]);
  const [name, setName] = useState(""); // State for input name

  useEffect(() => {
    getAllNames().then((names) => {
      setLessons(names);
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent default form submission
    createName(name).then((result) => {
      console.log("Name created:", result);
      setLessons((prevLessons) => [...prevLessons, result]); // Update state with new name
      setName(""); // Clear input field
    });
  };

  return (
    <div>
      <h1>This is the main stateful parent component.</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)} // Update state on input change
          placeholder="Enter a name"
          required
        />
        <button type="submit">Submit</button>
      </form>
      <MainList lessons={lessons} />
    </div>
  );
};

export default Main;
