// src/Components/Trainer/TrainerService.js
import Parse from "parse";

export const createTrainer = (name, pokemon) => {
  const Trainer = Parse.Object.extend("Trainer");
  const trainer = new Trainer();
  trainer.set("name", name);
  trainer.set("pokemon", pokemon); // Assign Pokémon to Trainer
  return trainer.save();
};

export const getTrainers = () => {
  const Trainer = Parse.Object.extend("Trainer");
  const query = new Parse.Query(Trainer);
  return query
    .find()
    .then((results) => {
      return results.map((item) => ({
        id: item.id,
        name: item.get("name"),
        pokemon: item.get("pokemon"),
      }));
    })
    .catch((error) => {
      console.error("Error fetching trainers: ", error);
    });
};
