// src/Components/Trainer/Trainer.js

import React, { useState, useEffect } from "react";
import { getTrainers, createTrainer, deleteTrainer } from "./TrainerService";
import MainList from "./MainList";
import "./Trainer.css";

const Trainer = () => {
  const [trainers, setTrainers] = useState([]);
  const [newName, setNewName] = useState("");
  const [newExperience, setNewExperience] = useState("");

  useEffect(() => {
    getTrainers().then((fetchedTrainers) => {
      setTrainers(fetchedTrainers);
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newName.trim() && newExperience.trim()) {
      createTrainer(newName, newExperience).then(() => {
        getTrainers().then((updatedTrainers) => {
          setTrainers(updatedTrainers);
        });
        setNewName("");
        setNewExperience("");
      });
    } else {
      alert("Please fill out all fields.");
    }
  };

  const handleDelete = (id) => {
    deleteTrainer(id).then(() => {
      setTrainers(trainers.filter((trainer) => trainer.id !== id));
    });
  };

  return (
    <div className="container">
      <div className="left">
        <h3>Submit New Trainer</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
            placeholder="Enter Trainer Name"
          />
          <input
            type="number"
            value={newExperience}
            onChange={(e) => setNewExperience(e.target.value)}
            placeholder="Enter Experience Level"
          />
          <button type="submit">Add Trainer</button>
        </form>
      </div>
      <div className="divider"></div>
      <div className="right">
        <h3>Trainer List</h3>
        <MainList trainers={trainers} onDelete={handleDelete} />
      </div>
    </div>
  );
};

export default Trainer;
