// src/Components/Pokemon.js

import React, { useState, useEffect } from "react";
import { getPokemon, createPokemon, deletePokemon } from "./LearnService";
import MainList from "./MainList";
import "./Main.css"; // Import the CSS file for styling

const Pokemon = () => {
  const [pokemons, setPokemons] = useState([]);
  const [newName, setNewName] = useState("");
  const [newType, setNewType] = useState("");
  const [newLocation, setNewLocation] = useState("");

  useEffect(() => {
    loadPokemon();
  }, []);

  const loadPokemon = () => {
    getPokemon().then((fetchedPokemon) => {
      setPokemons(fetchedPokemon);
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newName.trim() && newType.trim() && newLocation.trim()) {
      createPokemon(newName, newType, newLocation).then(() => {
        loadPokemon();
        setNewName("");
        setNewType("");
        setNewLocation("");
      });
    } else {
      alert("Please fill out all fields.");
    }
  };

  const handleDelete = (id) => {
    deletePokemon(id).then(() => {
      loadPokemon(); // Refresh the list after deletion
    });
  };

  return (
    <div className="container">
      <h3>Submit New Pokémon</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          placeholder="Enter Pokémon Name"
        />
        <input
          type="text"
          value={newType}
          onChange={(e) => setNewType(e.target.value)}
          placeholder="Enter Pokémon Type"
        />
        <input
          type="text"
          value={newLocation}
          onChange={(e) => setNewLocation(e.target.value)}
          placeholder="Enter Location Found"
        />
        <button type="submit">Add Pokémon</button>
      </form>

      <h3>Pokémon List</h3>
      <MainList pokemons={pokemons} onDelete={handleDelete} />
    </div>
  );
};

export default Pokemon;
